function onTick() a = input.getNumber(7) b = input.getNumber(8) c = input.getNumber(9) col = hsv2rgb(a,b,c) mode = input.getBool(5)
output.setNumber(1,mode and col.a or a) output.setNumber(2,mode and col.b or b) output.setNumber(3,mode and col.b or b) output.setBool(1,input.getBool(6)) end
function hsv2rgb(f,g,h)local a,b,c;local j=math.floor(f*6)local k=f*6-j;local l=h*(1-g)local m=h*(1-k*g)local n=h*(1-(1-k)*g)j=j%6;if j==0 then a,b,c=h,n,l elseif j==1 then a,b,c=m,h,l elseif j==2 then a,b,c=l,h,n elseif j==3 then a,b,c=l,m,h elseif j==4 then a,b,c=n,l,h elseif j==5 then a,b,c=h,l,m end;return{a=a,b=b,c=c}end
